import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const toggleMenu = () => {
    setIsMenuOpen(prev => !prev);
  };

  // Закриваємо меню при кліку за його межами
  useEffect(() => {
    const handleClickOutside = event => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    if (isMenuOpen) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [isMenuOpen]);

  return (
    <div className='container'>
      <nav className='navbar'>
        <Link to='/'>
          <img
            src='/images/logo.png'
            alt='Логотип'
            className='navbar__main-logo'
          />
        </Link>

        <div ref={menuRef}>
          <ul className={`navbar__menu ${isMenuOpen ? 'active' : ''}`}>
            <li className='navbar__link'>
              <Link to='/'>Головна</Link>
            </li>
            <li className='navbar__link'>
              <Link to='/articles'>Статті</Link>
            </li>
            <li className='navbar__link'>
              <Link to='/video-lessons'>Відеоуроки</Link>
            </li>
            <li className='navbar__link'>
              <Link to='/gallery'>Галерея</Link>
            </li>
          </ul>
        </div>

        {/* Кнопка з іконками ☰ / ✖ */}
        <button
          className='navbar__toggle'
          onClick={toggleMenu}
          aria-label='Перемкнути меню'
        >
          {isMenuOpen ? '✖' : '☰'}
        </button>
      </nav>
    </div>
  );
}

export default Header;
