import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null); // ✅ Створюємо референс для меню

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // ✅ Закриваємо меню при натисканні за його межами
  useEffect(() => {
    const handleClickOutside = event => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    if (isMenuOpen) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [isMenuOpen]);

  return (
    <div className='container'>
      <nav className='navbar'>
        <Link to='/'>
          <img
            src='/images/logo.png'
            alt='Logo'
            className='navbar__main-logo'
          />
        </Link>

        <div ref={menuRef}>
          {' '}
          {/* ✅ Обгортаємо меню у ref */}
          <ul className={`navbar__menu ${isMenuOpen ? 'active' : ''}`}>
            <li className='navbar__link'>
              <Link to='/'>Головна</Link>
            </li>
            <li className='navbar__link'>
              <Link to='/articles'>Статті</Link>
            </li>
            <li className='navbar__link'>
              <Link to='/video-lessons'>Відеоуроки</Link>
            </li>
            <li className='navbar__link'>
              <Link to='/gallery'>Галерея</Link>
            </li>
          </ul>
        </div>

        <button className='navbar__toggle' onClick={toggleMenu}>
          <span className='navbar__toggle-icon'></span>
        </button>
      </nav>
    </div>
  );
}

export default Header;
